msg.payload = msg.payload.Schedule.HeatPump.Sensor.State;
if (msg.payload == "on"){
    msg.label = "on";
    //node.send({ ui_control: { oncolor: "#ff0000" }, label: "on", payload: true });   
} else {
    //node.send({ ui_control: { offcolor: "#008000" }, label: "off", payload: false});
    msg.label = "off";
}
msg.oncolor = "#ff0000";
msg.offcolor = "008000";
return msg;

var state = context.get("state")||"OFF";

//  Look for a !X message to get values -- no button push.
if ( msg.topicSet == "ON" || msg.topicSet == "OFF"){
    context.set("colourOFF", msg.colourOFF);
    context.set("colourON", msg.colourON);
    context.set("textOFF", msg.textOFF);
    context.set("textON", msg.textON);
    context.set("fontOFF", msg.fontOFF);
    context.set("fortON", msg.fontON);
    context.set("payloadOFF", msg.payloadOFF);
    context.set("payloadON", msg.payloadON);
    state = msg.topicSet;
}
// If button pushed change state.
if ( msg.payload == "X" )
{
    if (state == "OFF") {
        state = "ON";
    } else { // topicSet should be "STOP"
        state = "OFF"
    }
    context.set("state", state);
}
if (state == "OFF")
{
    //  Condition OFF
    msg.payload = context.get("payloadOFF");
    msg.colour = context.get("colourOFF");
    msg.txt = context.get("textOFF");
    msg.fontclr = context.get("fontOFF");
} else
{
    //  Condition ON
    msg.payload = context.get("payloadON");
    msg.colour = context.get("colourON");
    msg.txt = context.get("textON");
    msg.fontclr = context.get("fontON");
}

return msg;

